import React, {useState, useEffect} from 'react'
import { API, apiKey } from '../../helpers/getAPI';
import { useForm } from '../../hooks/useForm'
import useModal from '../../hooks/useModal';
import { Modal } from '../UI/Modal'

export const FormRegister = ({dataTarget}) => {

    // Modal
    const [isOpenModal, openModal, closeModal] = useModal(false);
    const [sendData, setSendData] = useState(false);

    // Lógica formulario
    const [formValues, handleInputChange] = useForm({})
    const [yesEmail, setYesEmail] = useState("");
    const { name1, ape1, nacimiento1, curp1, meNombre1, meCedula1,
        farmaco1, telefono1, celular1, email1, password1, estado1,
        cp1, permission1, gender
    } = formValues

    const allInputs = () => {
        if (name1 && nacimiento1 && farmaco1 && curp1 && permission1
            && email1 && password1 && ape1 && cp1 && meNombre1
            && gender && estado1 && dataTarget) {
            setSendData(true);
        } else {
            setSendData(false);
        }
    }

    useEffect(() => {
        allInputs()
    }, [formValues, dataTarget]);

    useEffect(() => {
        if (email1?.length === 0) {
            setYesEmail("El Email es obligatorio");
        } else {
            setYesEmail("");
        }
    }, [email1]);

    const handleOutTarjet = async (e) => {
        e.preventDefault()
        const dateArray = nacimiento1.split('-')
        const nacimiento = `${dateArray[0]}${dateArray[1]}${dateArray[2]}`
        const dataLogin = {
            correo: email1,
            password: password1
        }
        if (!email1?.includes('@')) {
            setYesEmail("El correo no es válido")
        } else {
            setYesEmail("")
            openModal()
            localStorage.setItem('dataLogin', JSON.stringify(dataLogin))
        }
        // try {
        //     const response = await fetch(API("setCardAffiliation"), {
        //         'headers': {
        //             'Access-Control-Allow-Origin': '*',
        //             'Content-Type': 'application/json'
        //         },
        //         method: "POST",
        //         body: JSON.stringify({
        //             cardnumber: dataTarget,
        //             key: apiKey,
        //             name: name1,
        //             lastname: ape1,
        //             birthdate: nacimiento,
        //             gender: gender,
        //             curp: curp1,
        //             nameother: "",
        //             lastnameother:"",
        //             doctorname: meNombre1,
        //             doctornumber: meCedula1,
        //             phone: telefono1,
        //             cellphone: celular1,
        //             email: email1,
        //             permission: permission1,
        //             state: estado1,
        //             zipcode: cp1,
        //             paytreatment: "1",
        //             institution: "",
        //             prescription: []
        //         })
        //     });
        //     if (response.status === 200) {
        //         const result = await response.json();
        //         console.log(result);
        //     }
        // } catch (error) {
        //     console.log(error);
        // }
    }

    return (
        <>
            <form onSubmit={handleOutTarjet}>
                <input
                    name='name1'
                    value={name1}
                    onChange={handleInputChange}
                    type='text'
                    placeholder='Nombre Completo*'
                />
                <input
                    name='ape1'
                    value={ape1}
                    onChange={handleInputChange}
                    type='text'
                    placeholder='Apellidos*'
                />
                <div onChange={handleInputChange} style={{
                        fontSize: '16px',
                        display: 'flex',
                        alignItems: 'center',
                        justifyContent: 'center',
                        margin: '10px 0 12px'
                    }}
                    className="aling-text"
                >
                    <input type="radio" value="1" name="gender" />
                    <p style={{
                        margin:'0',
                        padding: '0 5px'
                    }} >Masculino</p>
                    <input type="radio" value="2" name="gender" />
                    <p style={{
                        margin:'0',
                        padding: '0 5px'
                    }} >Femenino</p>
                </div>
                <div style={{
                        display: 'flex',
                        fontSize: '16px',
                        justifyContent: 'center',
                        alignItems: 'center',
                        width: '100%',
                        maxWidth: '300px'
                    }}
                    className="aling-text"
                >
                    <label style={{marginRight: '5px'}}>*Fecha de nacimiento</label>
                    <input
                        name='nacimiento1'
                        value={nacimiento1}
                        onChange={handleInputChange}
                        type='date'
                        placeholder='Fecha de nacimiento*'
                    />
                </div>
                <input
                    name='curp1'
                    value={curp1}
                    onChange={handleInputChange}
                    type='text'
                    placeholder='CURP*'
                />
                <p style={{
                        fontSize: '16px',
                        textAlign: 'center',
                        margin: '0',
                        width: '100%'
                    }}
                    className="aling-text"
                >Datos médicos</p>
                <input
                    name='meNombre1'
                    value={meNombre1}
                    onChange={handleInputChange}
                    type='text'
                    placeholder='Médico Nombre*'
                />
                <input
                    name='meCedula1'
                    value={meCedula1}
                    onChange={handleInputChange}
                    type='text'
                    placeholder='Médico Cédula*'
                />
                <input
                    name='farmaco1'
                    value={farmaco1}
                    onChange={handleInputChange}
                    type='text'
                    placeholder='Fármaco/producto prescrito*'
                />
                <input
                    name='telefono1'
                    value={telefono1}
                    onChange={handleInputChange}
                    type='text'
                    placeholder='Teléfono*'
                />
                <input
                    name='celular1'
                    value={celular1}
                    onChange={handleInputChange}
                    type='text'
                    placeholder='Celular*'
                />
                <input
                    name='email1'
                    value={email1}
                    onChange={handleInputChange}
                    type='text'
                    placeholder='Email*'
                />
                {yesEmail.length > 0 && (
                    <p>{yesEmail}</p>
                )}
                <input
                    name='password1'
                    value={password1}
                    onChange={handleInputChange}
                    type='password'
                    placeholder='Crear contraseña*'
                />
                <p style={{fontSize: '16px', margin: '0'}}>Permiso Contacto</p>
                <div onChange={handleInputChange} style={{
                        fontSize: '16px',
                        display: 'flex',
                        alignItems: 'center',
                        justifyContent: 'center',
                        padding: '10px 0 12px',
                        textAlign: 'center',
                        width: '100%'
                    }}
                    className="aling-text"
                >
                    <input type="radio" value="1" name="permission1" />
                    <p style={{
                        margin:'0',
                        padding: '0 5px'
                    }} >Sí</p>
                    <input type="radio" value="2" name="permission1" />
                    <p style={{
                        margin:'0',
                        padding: '0 5px'
                    }} >No</p>
                </div>
                <input
                    name='estado1'
                    value={estado1}
                    onChange={handleInputChange}
                    type='text'
                    placeholder='Estado'
                />
                <input
                    name='cp1'
                    value={cp1}
                    onChange={handleInputChange}
                    type='text'
                    placeholder='Código Postal'
                />
                <button
                    type='submit'
                    id='contact-submit'
                    className="btn-c btn-validate-email"
                    disabled={!sendData}
                >Enviar</button>
            </form>
            <Modal isOpen={isOpenModal} closeModal={closeModal}>
                <p>Se envío un correo de verificación a su e-mail/SMS</p>
                <p>Correo: {email1}</p>
                <button
                    onClick={()=>closeModal()}
                    className="close-modal-r"
                >Aceptar
                </button>
            </Modal>
        </>
    )
}
